const MORE_MOVIES_DESKTOP = 4;
const MORE_MOVIES_TABLET = 3;
const MORE_MOVIES_MOBILE = 2;
const SHORT_MOVIES = 40;
// eslint-disable-next-line no-useless-escape
const EMAIL_REGEX =
    "^[-a-z0-9!#$%&'*+/=?^_`{|}~]+(.[-a-z0-9!#$%&'*+/=?^_`{|}~]+)*@([a-z0-9]([-a-z0-9]{0,61}[a-z0-9])?.)*(aero|arpa|asia|biz|cat|com|coop|edu|gov|info|int|jobs|mil|mobi|museum|name|net|org|pro|tel|travel|[a-z][a-z])$";
const USERNAME_REGEX = '^[A-Za-zА-Яа-яЁё /s -]+$';

export {
    MORE_MOVIES_DESKTOP,
    MORE_MOVIES_TABLET,
    MORE_MOVIES_MOBILE,
    SHORT_MOVIES,
    EMAIL_REGEX,
    USERNAME_REGEX,
};
